import { EventEmitter } from 'events';
import AppDispatcher from '../dispatcher/AppDispatcher.js';

let missionJSON = require('../assets/data/mission.json');
// this is mock data, ideally we would call the api to set this value


console.log(missionJSON);
// const initialState = {
//     mission: JSON.parse(JSON.stringify(missionJSON)) 
// }

var storage = { 
    mission: missionJSON
 };

console.log(storage.mission);
var TripDetailStore = Object.assign({}, EventEmitter.prototype, {

    getTrip: () => missionJSON.trip,
    getDriver: () => missionJSON.driver,
    getVehicle: () => missionJSON.vehicle,
    getVibe: () => missionJSON.vibe,


    dispatcherId: AppDispatcher.register(payload => {
        var action = payload.action;

        switch(payload.type) {
            case '':

                TripDetailStore.emit('change');
                break;
            default:
                return null;
        }
    })
});

export default TripDetailStore;
